package b_10951;

import java.util.Scanner;

public class Main {
	//https://www.acmicpc.net/problem/10951
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		
		while(sc.hasNextInt()) {
			int num1 = sc.nextInt();
			int num2 = sc.nextInt();
			
			System.out.println(num1 + num2);
		}
		
	}

}
